import React, {useState} from 'react';
import './App.css';
import Objective from "./components/Card/Objective.js";


function App() {
    const newObj = {
        "title": "Objective 3",
        "id": 3,
        "task": [{
            "id": 5,
            "title": "task1",
            "specific": "sasdad",
            "measurable": "ad",
            "achievable": "ads",
            "relevant": "asd",
            "status": "asd"
        }, {
            "id": 4,
            "title": "task2",
            "specific": "sasdad",
            "measurable": "ad",
            "achievable": "ads",
            "relevant": "asd",
            "status": "asd"
        }]
    }

    const [objectives, setObjectives] = useState([
            {
                "title": "Objective 1",
                "id": 1,
                "task": [
                    {
                        "id": 3,
                        "title": "task1",
                        "specific": "sasdad",
                        "measurable": "ad",
                        "achievable": "ads",
                        "relevant": "asd",
                        "status": "asd"
                    },
                    {
                        "id": 4,
                        "title": "task2",
                        "specific": "sasdad",
                        "measurable": "ad",
                        "achievable": "ads",
                        "relevant": "asd",
                        "status": "asd"
                    }
                ]
            },
            {
                "title": "Objective 2",
                "id": 2,
                "task": [
                    {
                        "id": 5,
                        "title": "task12",
                        "specific": "sasdad",
                        "measurable": "ad",
                        "achievable": "ads",
                        "relevant": "asd",
                        "status": "asd"
                    },
                    {
                        "id": 6,
                        "title": "task22",
                        "specific": "sasdad",
                        "measurable": "ad",
                        "achievable": "ads",
                        "relevant": "asd",
                        "status": "asd"
                    }
                ]
            }]

    )

    const addObjective = () => {
        let old = [...objectives];
        old=[...objectives, newObj];
        setObjectives(old);
    };

    const deleteObjective = (id) => {
        let old = [...objectives];
        old = objectives.filter(objective => objective.id !== id);
        setObjectives(old);
    };

    return (
        <div>
            <div className="content">
                {objectives.map((objective, index) => {
                    return (
                        <div>
                            <Objective key={index} objective={objective}/>
                            <button onClick={() =>deleteObjective(objective.id)}>Delete objective!</button>
                        </div>
                )
                })}
            </div>
            <div>
                <button onClick={addObjective}>Add objective!</button>
            </div>
        </div>
    )
}

export default App;
