import React, {useState} from 'react';
import '../../styling/Objective.css';
import Task from "../../components/Task/Task.js"

function Objective(props) {
    const [tasks, setTasks] = useState(props.objective.task);

    const removeTask = index => {
        const newTasks = [...tasks];
        newTasks.splice(index, 1);
        setTasks(newTasks);
    };
    const tempTask={
        "id": 5,
        "title": "task12",
        "specific": [{"id":32, "text":"todo 1"},{"id":22, "text":"todo 2"}],
        "measurable": "ad",
        "achievable": "ads",
        "relevant": "asd",
        "status": "asd"
    }
    const addTask = text => {
        const newTasks = [...tasks, { text }];
        setTasks(newTasks);
    };
    return (
        <div className="Objective">
            <div className="Title">
                {props.objective.title}
            </div>
            {props.objective.task.map(task =>
                <Task removeTask={removeTask} key={task.id} task={task} />)}
            <button onClick={() =>addTask(props.objective.id)}>Add Task</button>
            <button onClick={() =>props.delete(props.objective.id)}>Delete objective</button>
        </div>
    );
}

export default Objective;
