import React, {useState} from 'react';
import '../../styling/Task.css';
import Specific from "./Specific";
import SpecificForm from "./SpecificForm";
function Task(props) {

    const [specifics, setSpecifics] = useState(props.task.specific);
    const addTodo = text => {
        const newSpecifics = [...specifics, { text }];
        setSpecifics(newSpecifics);
    };

    const completeTodo = index => {
        const newSpecifics = [...specifics];
        newSpecifics[index].isCompleted = !newTodos[index].isCompleted;
        setSpecifics(newSpecifics);
    };
    const removeTodo = index => {
        const newSpecifics = [...specifics];
        newSpecifics.splice(index, 1);
        setSpecifics(newSpecifics);
    };
    return (
        <div className="Task">
            <div className="TaskTitle">
                {props.task.title}
            </div>
            <div className="Specific">
                Specific:
                <div className="todo-list">
                    {specifics.map((specific, index) => (
                        <Specific
                            key={index}
                            index={index}
                            todo={specific}
                            completeTodo={completeTodo}
                            removeTodo={removeTodo}
                        />
                    ))}
                    <SpecificForm addTodo={addTodo} />
                </div>
            </div>
            <div className="Specific">
                Measurable: {props.task.measurable}
            </div>
            <div className="Specific">
                Achievable: {props.task.achievable}
            </div>
            <div className="Specific">
                Relevant: {props.task.relevant}
            </div>
            <div className="Specific">
                Status: {props.task.status}
            </div>
            <button onClick={() => props.removeTask(props.task.id)}>Delete task</button>
        </div>
    );
}

export default Task;
