import React, {useState} from 'react';
import '../../styling/Task.css';

function Task(props) {
    const [todos, setTodos] = useState(props.task.specific);
    const addTodo = text => {
        const newTodos = [...todos, { text }];
        setTodos(newTodos);
    };

    const completeTodo = index => {
        const newTodos = [...todos];
        newTodos[index].isCompleted = true;
        setTodos(newTodos);
    };

    const removeTodo = index => {
        const newTodos = [...todos];
        newTodos.splice(index, 1);
        setTodos(newTodos);
    };
    return (
        <div className="Task">
            <div className="TaskTitle">
                {props.task.title}
            </div>
            <div className="Specific">
                Specific: {props.task.specific}
            </div>
            <div className="Measurable">
                Measurable: {props.task.measurable}
            </div>
            <div className="Achievable">
                Achievable: {props.task.achievable}
            </div>
            <div className="Relevant">
                Relevant: {props.task.relevant}
            </div>
            <div className="Status">
                Status: {props.task.status}
            </div>
        </div>
    );
}

export default Task;
