import React from "react";
import '../../styling/Task.css';

function Specific({ todo, index, startTodo, completeTodo, removeTodo }) {

    return (
        <div
            className="todo"
            style={{ textDecoration: todo.isCompleted ? "line-through" : "" }}
        >
            {todo.text}

            <div>
                <button className="ButtonGreen" onClick={() => completeTodo(index)}>&#x2713;</button>
                <button className="ButtonAmber" onClick={() => startTodo(index)}>o</button>
                <button className="ButtonRed" onClick={() => removeTodo(index)}>&#x2717;</button>
            </div>
        </div>
    );
}
export default Specific;
